INSERT INTO `tb_block_contents` VALUES (395, 394, 'Block 1', 'CHÚNG TÔI LUÔN LÀM\r\n<span class=\"font-playball green\">Hài lòng Khách hàng</span>', 'Chúng tôi tạo một thiên đường Yoga đích thực với sự chuyên nghiệp cùng chất lượng, dịch vụ vượt trội giúp bạn loại bỏ căng thẳng, tăng cường sức khoẻ và cải thiện chất lượng cuộc sống mỗi ngày!\r\n\r\nYoga studio là trung tâm Yoga uy tín, mang đến năng lượng tích cực cho người tập. không gian lớp học thư giãn ấm cúng và giáo viên thân thiện. Bạn sẽ được thư giãn gắn kết sau một ngày dài làm việc.', 'custom', '{\"style\":null}', '/data/cms-image/Images/img_gt1.png', '/data/cms-image/Images/bg_gt1.png', NULL, NULL, '/data/cms-image/Images/bg_gt1.png', NULL, NULL, 1, NULL, 'delete', 1, 1, '2023-07-05 09:47:18', '2023-07-05 10:35:06');
INSERT INTO `tb_block_contents` VALUES (397, 394, 'Block 2', 'LỚP HỌC\r\n<span class=\"green\">YOGA STUDIO</span>', 'Lớp học yoga studio thoáng mát, rộng rãi, được thiết kế với những lớp tập đa dạng, phù hợp với mọi lứa tuổi học viên.', 'custom', '{\"style\":\"flex-lg-row-reverse\"}', '/data/cms-image/Images/img_gt2.png', '/data/cms-image/Images/bg_gt2.png', NULL, NULL, NULL, NULL, NULL, 2, NULL, 'delete', 1, 1, '2023-07-05 10:19:50', '2023-07-05 10:33:35');
